        private void agregar_op_Click(object sender, RoutedEventArgs e)
        {
            //se cargan los operarios con sus asignaciones en una lista
            List<TodoItem> items = new List<TodoItem>();
            int operarios_conteo = 0;
            foreach (TodoItem item in Operarios.Items)
            {
                items.Add(new TodoItem() { Title = item.Title, Completion = item.Completion, diferenciador = item.diferenciador, plana = item.plana, rana = item.rana, flat = item.flat, cover = item.cover, transfer = item.transfer, atracadora = item.atracadora, plancha = item.plancha, bonding = item.bonding, zig_zag = item.zig_zag, multiaguja = item.multiaguja, manual = item.manual, varias = item.varias });
                operarios_conteo = operarios_conteo + 1;
            }

            // si el codigo de persona existe entonce se cargan sus datos
            if (revision.Visibility == System.Windows.Visibility.Visible)
            {
            // se declaran las variables de conexion
            SqlConnection cn = new SqlConnection("Data Source=" + ConfigurationManager.AppSettings["servidor_ing"] + ";Initial Catalog=" + ConfigurationManager.AppSettings["base_ing"] + ";Persist Security Info=True;User ID=" + ConfigurationManager.AppSettings["usuario_ing"] + ";Password=" + ConfigurationManager.AppSettings["pass_ing"]);
            string sql = "select nombre, plana, rana, flat, cover, transfer, atracadora, plancha, bonding, zig_zag, multiaguja, manual, varias from eficiencia_codigo_maquina where codigo= '" + codigo_op.Text.ToString() + "'";
            cn.Open();
            SqlCommand cm = new SqlCommand(sql, cn);
            SqlDataReader dr = cm.ExecuteReader();
            //se realiza la consulta en sql del operario que se esta buscando
            while (dr.Read())
            {
                items.Add(new TodoItem() { Title = dr["nombre"].ToString(), Completion = 0, diferenciador = "op", plana = dr["plana"].ToString(), rana = dr["rana"].ToString(), flat = dr["flat"].ToString(), cover = dr["cover"].ToString(), transfer = dr["cover"].ToString(), atracadora = dr["atracadora"].ToString(), plancha = dr["plancha"].ToString(), bonding = dr["bonding"].ToString(), zig_zag = dr["zig_zag"].ToString(), multiaguja = dr["multiaguja"].ToString(), manual = dr["manual"].ToString(), varias = dr["varias"].ToString() });
                operarios_conteo = operarios_conteo + 1;
            };
            dr.Close();
            cn.Close();
            }
            //si no existe la persona consultada se ingresara el nombre que se escriba manualmente
            else
            {
                items.Add(new TodoItem() { Title = nombre_op.Text, Completion = 0, diferenciador = "op"});
                operarios_conteo = operarios_conteo + 1;

            }

            Operarios.ItemsSource = items;
            //se agrega el nuevo numero de operarios que se ha calculado
            operarios_.Content = operarios_conteo.ToString();
            operarios_2.Content = operarios_conteo.ToString();
            try
            {
                //se obtiene la eficiencia
                decimal calculo_efic = Math.Round(Convert.ToDecimal(sam_.Content) * (Convert.ToDecimal(piezas_de_corrida.Text) / Convert.ToDecimal(horas_de_corrida.Text)) / Convert.ToDecimal(operarios_conteo), 0);
                eficiencia_.Content = calculo_efic.ToString() + " %";
                eficiencia_2.Content = calculo_efic.ToString() + " %";
            }
            catch
            {
                eficiencia_.Content = 0;
                eficiencia_2.Content = 0;
            }

            //se limpia el nombre del operario que se agrego
            codigo_op.Text = "";
            nombre_op.Text = "";

        }

