            //se agrega el nuevo numero de operarios que se ha calculado
            operarios_.Content = operarios_conteo.ToString();
            operarios_2.Content = operarios_conteo.ToString();
            try
            {
                //se obtiene la eficiencia
                decimal calculo_efic = Math.Round(Convert.ToDecimal(sam_.Content) * (Convert.ToDecimal(piezas_de_corrida.Text) / Convert.ToDecimal(horas_de_corrida.Text)) / Convert.ToDecimal(operarios_conteo), 0);
                eficiencia_.Content = calculo_efic.ToString() + " %";
                eficiencia_2.Content = calculo_efic.ToString() + " %";
            }
            catch
            {
                eficiencia_.Content = 0;
                eficiencia_2.Content = 0;
            }
        }

